INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICIES_CULTURAL_VICTORY', 'Upon completing 6 Policy branches, you may construct the [COLOR_POSITIVE_TEXT]Utopia Project[ENDCOLOR] in one of your Cities, triggering a [COLOR_POSITIVE_TEXT]Cultural Victory[ENDCOLOR]!', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_LIBERTY_HELP', 'Good for quickly claiming early territory.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+1 Palace in oldest 3 cities.[NEWLINE]+1 [ICON_CULTURE] Culture in all cities.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+3 [ICON_PRODUCTION] Production in cities with a Specialist.[NEWLINE]+1 [ICON_TEAM_2] Great Person of choice.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_COLLECTIVE_RULE_HELP', '+1 [ICON_TEAM_2] Settler at the Capital.[NEWLINE]+20% [ICON_PRODUCTION] Production for Settlers.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_MERITOCRACY_HELP', 'Gives a lump sum of [ICON_GOLD] Gold.[NEWLINE][NEWLINE]Reduces policy costs in large empires, so cities with Theaters are revenue-neutral for policy rate.', '', '');
