INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_TRADE_PACT_TEXT', 'Free trade is a system of trade policy that allows traders to trade across national boundaries without interference from the respective governments. Due to the economic principle of comparative advantage, this provides trading partners with mutual gains. Under this policy prices are the sole determinant of resource allocation, and a close reflection of supply and demand.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_ORDER_HELP', 'Good for expanding and developing modern cities.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+10% [ICON_PRODUCTION] Production in all cities.[NEWLINE]+1 [ICON_TEAM_7] Great Engineer at the Capital.[NEWLINE]Can purchase Great Engineers with [ICON_PEACE] Faith.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]Gives 10 turns of [ICON_PRODUCTION] Production in all cities.[NEWLINE]+10% [ICON_PRODUCTION] Production from factories and power plants.', '', '');
