INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_RATIONALISM_TEXT', 'Knowledge is a familiarity with someone or something, which can include facts, information, descriptions, or skills acquired through experience or education. It can refer to the theoretical or practical understanding of a subject. It can be practical skill or expertise, or the theoretical understanding of a subject.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_PIETY_HEADING3_BODY', 'Piety increases the Happiness, Culture, and Faith of the adopting civ. Piety becomes available once the Classical Era is reached. This is an ideal place to acquire policies for players seeking to create or expand religions.', '', '');
