INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_HEADING3_BODY', 'The branch of Knowledge improves the civilization''s ability to use and generate Science, becoming available upon entering the Renaissance Era.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_TECH_RATIONALISM_HEADING4_BODY', 'The Knowledge branch of social policies is full of policies which can increase your research. This branch becomes available in the Renaissance era. See the rules on Social Policies for details.', '', '');
