INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_AUTOCRACY}', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_AUTOCRACY_TEXT', 'Nationalism emphasizes the importance and uniqueness of the state. The state is the mother and father of the people, and the people love the state. Everybody has the national flag flying from their home, and everybody attends the founding day parade of military personnel and hardware. Nationalist states are often strong, as the people work tirelessly for the state''s advancement. However, internal criticism of the state may be stifled, which can limit the state''s ability to correct itself if goes off course.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_ADV_QUEST', 'What is the Nationalism social policy about?', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_HEADING3_BODY', 'This branch is well suited for those struggling to keep their empire happy. It is ideal for players who seek a Domination Victory. This policy unlocks upon entering the Renaissance Era, and cannot be active at the same time as Freedom.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY_HELP', 'Good for happiness and conquest.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+1 [ICON_HAPPINESS_1] City Happiness from Monuments.[NEWLINE]Can purchase Great Generals and Admirals with [ICON_PEACE] Faith.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+30 [ICON_WAR] Experience for units.[NEWLINE][NEWLINE][COLOR_WARNING_TEXT]Nationalism cannot be active with Freedom.[ENDCOLOR]', '', '');
