INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_IMMIGRATION', 'Immigration', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_IMMIGRATION_HELP', 'City-State friends and allies gift [ICON_GREAT_PEOPLE] Great People.', '', '');
INSERT INTO CEP_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_IMMIGRATION_TEXT', 'Immigration is the movement of people into a country or region to which they are not native in order to settle there. Immigration is made for many reasons, including temperature, breeding, economic, political, family re-unification, natural disaster, poverty or the wish to change one''s surroundings voluntarily.[NEWLINE][NEWLINE]The reasons may be social environment (in source countries: lack of opportunities, political instability, economic depression, health risks, etc.; in host countries: rich opportunities, political stability and freedom, developed economy, better living conditions, etc.). In terms of individual reasons, there are family influence (overseas relatives), and personal preference: preference for exploring, ambition for an improved career, etc.[NEWLINE][NEWLINE]One specific type of immigration may be termed ''brain-drain''. This term was coined by the Royal Society to describe the emigration of "scientists and technologists" to North America from post-war Europe.', '', '');
