INSERT INTO Cep_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-12-5'), 'TXT_KEY_POLICY_WAR_EPICS_HELP', '+1 [ICON_HAPPINESS_1] City Happiness from Culture buildings.', '', '');
INSERT INTO Cep_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-12-5'), 'TXT_KEY_POLICY_WAR_EPICS_TEXT', 'An epic (from the Ancient Greek adjective ἐπικός (epikos), from ἔπος (epos) "word, story, poem") is a lengthy narrative poem, ordinarily concerning a serious subject containing details of heroic deeds and events significant to a culture or nation.[NEWLINE][NEWLINE]War epics are epic poems or a literary works of epic scope which seek or are believed to capture and express the essence or spirit of a particular nation, or an ethnic or linguistic group with aspirations to independence or autonomy. National epics frequently recount the origin of a nation, a part of its history, or a crucial event such as decisive victories or the vanquishing of bitter foes, in the development of national identity', '', '');
