INSERT INTO Cep_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-12-5'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
INSERT INTO Cep_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-12-5'), 'TXT_KEY_POLICY_COMMERCE_TEXT', 'Wealth is the abundance of valuable resources or material possessions. The concept of wealth is significant in all areas of economics, yet the meaning of wealth is context-dependent and there is no universally agreed upon definition. At the most general level wealth is &quot;anything of value&quot;, which captures both the subjective nature of the word, and the idea that it is not a fixed or static concept.', '', '');
